    const {
      unique = false,
      name,
      type = 'btree',
      where,
      concurrently = true,
      operator,
    } = options;

    const sequelize = queryInterface.sequelize;
    const dialect = sequelize.getDialect();

    const indexName = name || `idx_${tableName}_${columns.join('_')}`;

    if (dialect === 'postgres') {
      const uniqueClause = unique ? 'UNIQUE' : '';
