  transaction?: Transaction,
): Promise<void> {
  const { cascade = false, ifExists = true } = options;

  // Check if table exists
  if (ifExists) {
    const tableExists = await checkTableExists(queryInterface, tableName);
    if (!tableExists) {
      return;
    }
  }

  // Drop table
  await queryInterface.dropTable(tableName, {
    cascade,
    transaction,
  });
}

